
public class LearningVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte A1 = 100;
		System.out.println("The value of A1: " + A1);
		
		short A2 = 1000;
		System.out.println("The value of A2: " + A2);

		int A3 = 5000000;
		System.out.println("The value of A3: " + A3);		
		
		long A4 = 500000000000000000L;
		System.out.println("The value of A4: " + A4);

		float A5 = 20.0f;
		System.out.println("The value of A5: " + A5);

		double A6 = 25.50;
		System.out.println("The value of A6: " + A6);

		boolean A7 = true;
		System.out.println("The value of A7: " + A7);

		char A8 = 'a';
		System.out.println("The value of A8: " + A8);


	}

}
